import React from 'react';
import SearchArea from './Search/Search';
import Welcomecard from 'components/WelcomeCard/WelcomeCard';
import VisiCard from 'components/VisiCard/VisiCard';
import MisiCard from 'components/MisiCard/MisiCard';
import Loader from 'components/Loader/Loader';
import Location from './Location/Location';
import useDataApi from 'library/hooks/useDataApi';
import isEmpty from 'lodash/isEmpty';


const Home = ({ match }) => {
    let url = '/data/hotel-single.json';
    if (!match.params.slug) {
        url += match.params.slug;
    }
    const { data, loading } = useDataApi(url);
    if (isEmpty(data) || loading) return <Loader />;
    const {
        reviews,
        rating,
        ratingCount,
        price,
        title,
        gallery,
        location,
        content,
        // amenities,
        author,
    } = data[0];

    return (
        <>
        <SearchArea />
        <Welcomecard 
            title="Selamat Datang di Asosiasi Muthowif Indonesia"
            description="Kami adalah Asosiasi Muthowif Indonesia"
        />
        <VisiCard 
            title="Visi Kami"
            description="Kami adalah Asosiasi Muthowif Indonesia"
        />
        <MisiCard
            title="Misi Kami"
            description="Kami adalah Asosiasi Muthowif Indonesia"
        />
        <Location location={data[0]} />
        </>
    );
};

export default Home;
