import React from 'react';
import PropTypes from 'prop-types';
import { IoIosArrowBack } from 'react-icons/io';
import { IoIosArrowForward } from 'react-icons/io';
import Loader from 'components/Loader/Loader';
import Container from 'components/UI/Container/Container';
import Heading from 'components/UI/Heading/Heading';
import TextLink from 'components/UI/TextLink/TextLink';
import SectionTitle from 'components/SectionTitle/SectionTitle';
import ImageCard from 'components/ImageCard/ImageCard';
import GlideCarousel, {
	GlideSlide,
} from 'components/UI/GlideCarousel/GlideCarousel';
import useDataApi from 'library/hooks/useDataApi';
import { LISTING_POSTS_PAGE } from 'settings/constant';
import LocationWrapper, { CarouselSection } from './ImageCarousel.style';
const carouselOptions = {
	type: 'carousel',
	perView: 5,
	gap: 30,
	hoverpause: true,
	breakpoints: {
		1440: {
		perView: 5,
		gap: 20,
		},
		1200: {
		perView: 4,
		},
		991: {
		perView: 3,
		gap: 15,
		},
		667: {
		perView: 2,
		gap: 20,
		},
		480: {
		perView: 5,
		gap: 0,
		},
	},
};

const ImageCarousel = ({ title, useLink, linkText, dataApi }) => {
	const { data } = useDataApi(dataApi);

	return (
		<LocationWrapper>
		<Container fluid={true}>
			<SectionTitle
				title={<Heading content={title} />}
				link={useLink && (<TextLink link={LISTING_POSTS_PAGE} content={linkText} />)}
			/>

			<CarouselSection>
			{data.length !== 0 ? (
				<GlideCarousel
				carouselSelector="explore_carousel"
				prevButton={<IoIosArrowBack />}
				nextButton={<IoIosArrowForward />}
				options={carouselOptions}
				>
				<>
					{data.map((post, index) => (
					<GlideSlide key={index}>
						<ImageCard
						link="listing"
						imageSrc={post.locationImage.url}
						title={post.city}
						meta={`${post.numberOfPost} Hotels`}
						/>
					</GlideSlide>
					))}
				</>
				</GlideCarousel>
			) : (
				<Loader />
			)}
			</CarouselSection>
		</Container>
		</LocationWrapper>
	);
};

ImageCarousel.propTypes = {
	title: PropTypes.string,
	useLink: PropTypes.string,
	linkText: PropTypes.string,
	dataApi: PropTypes.string
}

ImageCarousel.defaultProps = {
	dataApi: '/data/location.json',
};

export default ImageCarousel;
